import React, { useState, useEffect } from "react";
import './/SearchPage.css'
import { observer } from "mobx-react";
import Card from "react-bootstrap/Card";
import { Formik } from "formik";
import Form from "react-bootstrap/Form";
import Col from "react-bootstrap/Col";
import Button from "react-bootstrap/Button";
import * as yup from "yup";
import { Redirect } from "react-router-dom";
const querystring = require("querystring");

const schema = yup.object({
    name: yup.string().required("URL is required"),
    url: yup
      .string()
      .required("URL is required")
      .matches(
        /(https?:\/\/)?([\w\-])+\.{1}([a-zA-Z]{2,63})([\/\w-]*)*\/?\??([^#\n\r]*)?#?([^\n\r]*)/,
        "Invalid URL"
      ),
  });
  
function SearchPage({ searchsStore }) {
    const [initialized, setInitialized] = useState(false);
    const [redirectToSearch, setRedirectToSearch] = useState(false);
  
    const handleSubmit = async evt => {
      const isValid = await schema.validate(evt);
      if (!isValid) {
        return;
      }
      searchsStore.searchs.push(evt);
      searchsStore.setSearchs(searchsStore.searchs);
      localStorage.setItem("searchs", JSON.stringify(searchsStore.searchs));
    };
  
    const setSelectedSearch = url => {
        searchsStore.setSelectedSearch(url);
      setRedirectToSearch(true);
    };
  
    const deleteSearch = index => {
        searchsStore.searchs.splice(index, 1);
      searchsStore.setSearchs(searchsStore.searchs);
      localStorage.setItem("searchs", JSON.stringify(searchsStore.searchs));
    };
  
    useEffect(() => {
      if (!initialized) {
        let rssSearchs = [];
        try {
          rssSearchs = JSON.parse(localStorage.getItem("searchs"));
          if (Array.isArray(rssSearchs)) {
            searchsStore.setSearchs(rssSearchs);
          }
        } catch (ex) {}
        setInitialized(true);
      }
    });
  
    if (redirectToSearch) {
      return (
        <Redirect to={`/feed?${querystring.encode({ url: searchsStore.search })}`} />
      );
    }
  
    return (

        <div className= {`Search ${theme}` }>
        <div className={`nav ${theme2}`}>
         <div className="logo">RSS-Feed<span>.</span></div> 
         
         <ul>
           <li> <a href ="/" id = "home-link" >
                        Home
                      </a> </li>
           <li> <a href ="/feed" id = "feed-link" >
                        Feed
                       </a> </li>
           <li> <a href ="/search" id = "search-link" >
                        Search
                       </a> </li>   
           <li> <a href ="/searchP" id = "search-link" >
                        SearchP
                       </a> </li>         
          
         </ul>
         </div>
      


      <div className="search-page">
        <h1 className="center">RSS Feed</h1>
        <Formik validationSchema={schema} onSubmit={handleSubmit}>
          {({
            handleSubmit,
            handleChange,
            handleBlur,
            values,
            touched,
            isInvalid,
            errors,
          }) => (
            <Form noValidate onSubmit={handleSubmit}>
              <Form.Row>
                <Form.Group as={Col} md="12" controlId="name">
                  <Form.Label>Name</Form.Label>
                  <Form.Control
                    type="text"
                    name="name"
                    placeholder="Name"
                    value={values.name || ""}
                    onChange={handleChange}
                    isInvalid={touched.name && errors.name}
                  />
                  <Form.Control.Feedback type="invalid">
                    {errors.name}
                  </Form.Control.Feedback>
                </Form.Group>
                <Form.Group as={Col} md="12" controlId="url">
                  <Form.Label>URL</Form.Label>
                  <Form.Control
                    type="text"
                    name="url"
                    placeholder="URL"
                    value={values.url || ""}
                    onChange={handleChange}
                    isInvalid={touched.url && errors.url}
                  />
  
                  <Form.Control.Feedback type="invalid">
                    {errors.url}
                  </Form.Control.Feedback>
                </Form.Group>
              </Form.Row>
              <Button type="submit">Add</Button>
            </Form>
          )}
        </Formik>
        <br />
        {searchsStore.searchs.map((f, i) => {
          return (
            <Card key={i}>
              <Card.Title className="card-title">{f.name}</Card.Title>
              <Card.Body>
                <p>{f.url}</p>
                <Button
                  variant="primary"
                  onClick={setSelectedSearch.bind(this, f.url)}
                >
                  Open
                </Button>{" "}
                <Button variant="primary" onClick={deleteSearch.bind(this, i)}>
                  Delete
                </Button>
              </Card.Body>
            </Card>
          );
        })}
      </div>
      <Footer/>
      </div>
    );
  }

//export default SearchPage;
export default observer(SearchPage);
