//UCYmET81J6olojeOvXsi_n8g


import './/Lectures.css'
import React, { useState, useEffect } from "react";
import Card from "react-bootstrap/Card";

import Button from "react-bootstrap/Button";



import { parseDate } from "./util";

const youtubeRssFeed =
  "https://api.rss2json.com/v1/api.json?rss_url=https://youtube.com/feeds/videos.xml?channel_id=UCYmET81J6olojeOvXsi_n8g";

const useStyles = makeStyles(() => ({
  root: {
    maxWidth: "100%",
    margin: "1.5rem",
    backgroundColor: "#fef6e4",
    color: "#f582ae"
  },
  media: {
    height: 300,
    minWidth: "100%"
  }
}));

const Lectures = () => {
  const classes = useStyles();
  const MAX_VIDEOS = 10;

  const [videos, setVideos] = useState();

  useEffect(() => {
    const loadVideos = async () => {
      fetch(youtubeRssFeed, { headers: { Accept: "application/json" } })
        .then((res) => res.json())
        .then((data) => data.items.filter((item) => item.title.length > 0))
        .then((newVideos) => newVideos.slice(0, MAX_VIDEOS))
        .then((videos) => setVideos(videos))
        .catch((error) => console.log(error));
    };

    loadVideos();
  }, [MAX_VIDEOS]);

  return (
    <Container maxWidth="sm">
      <h2 className="heading">Latest Videos</h2>

      {videos
        ? videos.map((item) => (
            <a
              className="link"
              href={item.link}
              target="_blank"
              rel="nofollow noopener noreferrer"
              title={item.title}
              aria-label={item.link}
              key={item.link}
            >
              <Card className={classes.root}>
                <CardActionArea>
                  <CardMedia
                    className={classes.media}
                    image={item.thumbnail}
                    title={item.title}
                  />
                  <CardContent>
                    <Typography gutterBottom variant="h5" component="h2">
                      {item.title}
                    </Typography>
                    <Typography
                      variant="body2"
                      color="textSecondary"
                      component="p"
                    >
                      {parseDate(item.pubDate)}
                    </Typography>
                  </CardContent>
                </CardActionArea>
                <CardActions>
                  <FacebookShareButton url={item.link}>
                    <FacebookIcon size={32} round={true} />
                  </FacebookShareButton>

                  <Button size="small" color="primary">
                    Learn More
                  </Button>
                </CardActions>
              </Card>
            </a>
          ))
        : "no video shown"}
     
    </Container>
  );
};

export default Lectures;
