import React, {useEffect, useState, useContext } from 'react';
import { ThemeContext } from "../contexts/ThemeContext";
import './/Feed.css'
import Footer from './Footer'
import { observer } from "mobx-react";
//import { Router } from "react-router-dom";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import * as yup from "yup";
import { getFeedListing } from "../contexts/requests";
const querystring = require("querystring");

function Feed ({feedsStore, location}) {
    const [backEndData, setBackEndData] = useState([{}])
    const [initialized, setInitialized] = useState(false);
    const [url, setUrl] = useState("");
    const [listings, setListings] = useState([]);
    const [data, setData] = useState({});
    const  querystring = require("querystring");
    const getListings = async url => {
        try {
          const response = await getFeedListing(url);
          setListings(response.data.items);
          setData(response.data.feed);
        } catch (ex) {
          console.log(ex);
        }
      };
    
    const openLink = url => {
        window.location.href = url;
    };
    
    useEffect(() => {
        if (!initialized) {
          const url = querystring.decode(location.search)["?url"];
          setUrl(url);
          getListings(url);
          setInitialized(true);
        }
      });
   
   
      
    const context = useContext(ThemeContext);
    const theme = context.isLightTheme ? context.light : context.dark;
    const theme2 = context.isLightTheme ? context.cardLight : context.cardDark;
    const theme3 = context.isLightTheme ? context.pageLight : context.pageDark;

    const ThemeToggler = (props) => {
      const context = useContext(ThemeContext);
      const btnText = context.isLightTheme ? "Light ☀️" : "Dark 🌘";
      const toggleTheme = context.toggleTheme;
      
    
      return (
        <button className={`button is-light rounded`} onClick={toggleTheme}>
          {btnText}
        </button>
      );
    };

/*
    useEffect(() => {
      // A proxy was define in package.json so we dont need to put the full route. This will be changed in production
      fetch("/api").then(
        response => response.json()
      ).then(
        data => {
          setBackEndData(data)
        }
      )
    }, [])
    */
    return (
        
         <div className= {`Feed ${theme}` }>
         <div className={`nav ${theme2}`}>
          <div className="logo">RSS-Feed<span>.</span></div> 
          
          <ul>
            <li> <a href ="/" id = "home-link" >
                         Home
                       </a> </li>
            <li> <a href ="/feed" id = "feed-link" >
                         Feed
                       </a> </li>
           
          </ul>
          </div>
       
          <div className={`${theme3}`}>
          <div className={theme3}>
          <ThemeToggler className="themeBtn"/>  
          </div>
         
          
            
          
       
       </div>
        <Footer/>
       </div>
       

    );
}

export default Feed;
